import React, {useState} from "react";

function ButtonHandler({dataTestId, spanClass, className, type, focusDuration, setFocusDuration}) {
    const [disabled, setDisabled] = useState(false);
    const handleClick = () => {
        if (dataTestId.includes("increase-focus")){
            if (focusDuration > 59){
                setDisabled(true);
            } else {
                setDisabled(false);
                setFocusDuration(focusDuration + 1);
                console.log("focus duration + 1");
            }
        } else if (dataTestId.includes("decrease-focus")){
            if (focusDuration < 6){
                setDisabled(true);
            } else {
                setDisabled(false);
                setFocusDuration(focusDuration - 1);
                console.log("focus duration - 1");
            }
        }
    };

    return (
        <button
        type={type}
        className={className}
        data-testid={dataTestId}
        onClick={handleClick}
        disabled={disabled}
        >
            <span className={spanClass} />
        </button>
    );
};

export default ButtonHandler;
