import React, {useState} from "react";

function ButtonHandler({dataTestId, spanClass, className, type, focusDuration, setFocusDuration}) {
    const [disabled, setDisabled] = useState(false);
    const handleClick = () => {
        if (dataTestId.includes("increase")){
            if (focusDuration === 5 || focusDuration === 60){
                setDisabled(true);
            } 
        }
    };

    return (
        <button
        type={type}
        className={className}
        data-testid={dataTestId}
        onClick={handleClick}
        disabled={disabled}
        >
            <span className={spanClass} />
        </button>
    );
};

export default ButtonHandler;
